mapper.update:
UPDATE MCC907
SET  UPDATE_DT = NOW()
    ,UPDATE_ID = #{updateId}
    ,PROD_SEQ = #{prodSeq}
	,OPER_CD = #{operCd}
	,OPER_SEQ = #{operSeq}
	,BOM_LEVEL = #{bomLevel}
	,OPER_UPCD = #{operUpcd}
	,OPER_UPCD_SEQ = #{operUpcdSeq}
	,OPER_TOPCD = #{operTopcd}
	,OPER_TOPCD_SEQ = #{operTopcdSeq}
	,ITEM_CD = #{itemCd}
	,BOM_VER = #{bomVer}
	,BOM_STDT = #{bomStdt}
	,OUTCUST_YN = #{outcustYn}
	,CUST_CD = #{custCd}
	,OUTSIDE_OUTDT = #{outsideOutdt}
	,OUTSIDE_INDT = #{outsideIndt}
	,MOLD_CD = #{moldCd}
	,MOLD_SEQ = #{moldSeq}
	,EQUIP_CD = #{equipCd}
	,EQUIP_SEQ = #{equipSeq}
	,WORK_DT = TO_CHAR(now(),'YYYYMMDD')
	,WORKST_DT = TO_CHAR(now(),'YYYYMMDDHH24MMSS')
	,WORKED_DT = TO_CHAR(now(),'YYYYMMDDHH24MMSS')
	,PROD_TYPE_CD = #{prodTypeCd}
	,SABUN_ID = #{sabunId}
	,PO_QTY = #{poQty}
	,ACTOK_QTY = #{actokQty}
	,ACTBAD_QTY = #{actbadQty}
	,BAD_CD = #{badCd}
	,WORKACT_BIGO = #{workactBigo}
	,CONFIRM_YN = #{confirmYn}
	,USE_YN = #{useYn}
WHERE 1=1
  AND FACTORY_CD = #{factoryCd}
  AND WORKACT_SEQ = #{workactSeq}

UPDATE MCC907
SET  UPDATE_DT = NOW()
    ,UPDATE_ID = #{updateId}
    ,USE_YN = 'N'
WHERE 1=1
  AND FACTORY_CD = #{factoryCd}
  AND WORKACT_SEQ = #{workactSeq}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY WRITE_DT DESC) AS RNUM , PLC_HIS_SEQ , PLC_CD , PLC_TYPE_CD , PLC_STATUS_CD , PROD_SEQ , DATA1 , DATA2 , DATA3 , WRITE_DT , TO_CHAR(WRITE_DT, 'YYYY/MM/DD HH24:MI:SS') AS WRITE_DATE FROM (SELECT PLC_HIS_SEQ , PLC_CD , PLC_TYPE_CD , PLC_STATUS_CD , PROD_SEQ , CAST( REGEXP_REPLACE(DATA1, '\s', '', 'g') AS INTEGER ) AS DATA1 , CAST( REGEXP_REPLACE(DATA2, '\s', '', 'g') AS INTEGER ) AS DATA2 , CAST( REGEXP_REPLACE(DATA3, '\s', '', 'g') AS INTEGER ) AS DATA3 , DENSE_RANK() OVER( PARTITION BY DATA1, DATA2, DATA3 ORDER BY WRITE_DT DESC ) AS GROUP_RNUM , WRITE_DT FROM MCC908 WHERE PLC_CD = 'EQUIPMENT1' AND PLC_TYPE_CD = 'P' AND REGEXP_REPLACE(DATA1, '\s+$', '') != ''
AND PROD_SEQ = #{prodSeq}
AND WRITE_DT BETWEEN TO_TIMESTAMP(#{workDt}||'000000','YYYYMMDDHH24MISS') AND TO_TIMESTAMP(#{workDt}||'235959','YYYYMMDDHH24MISS') ) A WHERE GROUP_RNUM = 1

SELECT COUNT(*) AS TOT_CNT FROM (SELECT PLC_HIS_SEQ , PLC_CD , CAST( REGEXP_REPLACE(WORK_CNT, '\s', '', 'g') AS INTEGER ) AS WORK_CNT , CAST( REGEXP_REPLACE(ERR_CNT, '\s', '', 'g') AS INTEGER ) AS ERR_CNT , DENSE_RANK() OVER( PARTITION BY WORK_CNT, ERR_CNT ORDER BY WRITE_DT DESC ) AS ROW_NUM , WRITE_DT FROM MCC908 WHERE EQUIP_CD = #{equipCd} AND REGEXP_REPLACE(WORK_CNT, '\s+$', '') != ''
AND PROD_SEQ = #{searchProdSeq}
AND WRITE_DT BETWEEN TO_TIMESTAMP(#{searchFromDate}||'000000','YYYYMMDDHH24MISS') AND TO_TIMESTAMP(#{searchToDate}||'235959','YYYYMMDDHH24MISS') ) A WHERE ROW_NUM = 1
